mapper.sql:
WHERE 1=1
     AND ba.id = #{id}
     AND ba.area_code LIKE CONCAT('%','${areaCode}','%')
     AND ba.area_name LIKE CONCAT('%','${areaName}','%')
     AND ba.remark LIKE CONCAT('%','${remark}','%')
     AND ba.company_id = #{companyId}
     AND ba.warehouse_id = #{warehouseId}
    AND (ba.area_name LIKE CONCAT('%','${fuzzyCondition}','%')
     OR ba.area_code LIKE CONCAT('%','${fuzzyCondition}','%'))

WHERE 1=1
     AND ba.id = #{id}
     AND ba.area_code = #{areaCode}
     AND ba.area_name = #{areaName}
     AND ba.company_id = #{companyId}

mapper.select:
select
from bd_area ba WHERE id in
#{item}

mapper.update:
	UPDATE bd_area




                     when (ID=#{item.id}) then #{item.areaCode}




                     when (ID=#{item.id}) then #{item.areaName}




                     when (ID=#{item.id}) then #{item.remark}






                     when (ID=#{item.id}) then #{item.modifier}




                     when (ID=#{item.id}) then #{item.modifyTime}




                     when (ID=#{item.id}) then #{item.recVer}




                     when (ID=#{item.id}) then #{item.companyId}




                     when (ID=#{item.id}) then #{item.warehouseId}



	WHERE


		(ID=#{item.id}) AND (REC_VER=#{item.recVer}-1)

