mapper.sql:
WHERE 1=1
     AND wid.id = #{id}
     AND wid.order_no LIKE CONCAT('%', '${orderNo}','%')
     AND wid.supplier_code = #{supplierCode}
     AND wid.line_no LIKE CONCAT('%','${lineNo}','%')
     AND wid.sku_code LIKE CONCAT('%','${skuCode}','%')
     AND wid.is_created_voucher = #{isCreatedVoucher}
     AND wid.voucher_no LIKE CONCAT('%','${voucherNo}','%')
     AND wid.company_id = #{companyId}
     AND wid.warehouse_id = #{warehouseId}
    AND (wid.order_no LIKE CONCAT('%','${fuzzyCondition}','%')
    OR wid.line_no LIKE CONCAT('%','${fuzzyCondition}','%') 
    OR wid.sku_code LIKE CONCAT('%','${fuzzyCondition}','%'))

WHERE 1=1
     AND wid.id = #{id}
     AND wid.order_no = #{orderNo}
     AND wid.supplier_code = #{supplierCode}
     AND wid.line_no = #{lineNo}
     AND wid.sku_code = #{skuCode}
     AND wid.is_created_voucher = #{isCreatedVoucher}
     AND wid.voucher_no = #{voucherNo}
     AND wid.company_id = #{companyId}
     AND wid.warehouse_id = #{warehouseId}

