mapper.update:
	UPDATE wm_inbound_detail




                     when (ID=#{item.id}) then #{item.orderNo}




                     when (ID=#{item.id}) then #{item.lineNo}




                     when (ID=#{item.id}) then #{item.status}




                     when (ID=#{item.id}) then #{item.supplierCode}




                     when (ID=#{item.id}) then #{item.skuCode}




                     when (ID=#{item.id}) then #{item.inboundPreNum}




                     when (ID=#{item.id}) then #{item.inboundNum}




                     when (ID=#{item.id}) then #{item.inboundPrice}




                     when (ID=#{item.id}) then #{item.cost}




                     when (ID=#{item.id}) then #{item.planLoc}




                     when (ID=#{item.id}) then #{item.isCreatedVoucher}




                     when (ID=#{item.id}) then #{item.voucherNo}




                     when (ID=#{item.id}) then #{item.isCrossDock}




                     when (ID=#{item.id}) then #{item.remark}




                     when (ID=#{item.id}) then #{item.modifier}




                     when (ID=#{item.id}) then #{item.modifyTime}




                     when (ID=#{item.id}) then #{item.recVer}




                     when (ID=#{item.id}) then #{item.companyId}




                     when (ID=#{item.id}) then #{item.warehouseId}



	WHERE


		(ID=#{item.id}) AND (REC_VER=#{item.recVer}-1)

	UPDATE wm_inbound_detail wid set wid.status = #{status,jdbcType=VARCHAR},
	wid.rec_ver = wid.rec_ver+1 
	where wid.line_no in (#{lineNos,jdbcType=VARCHAR})
	and 
	wid.order_no = #{orderNo,jdbcType=VARCHAR}
	and 
	wid.company_id = #{companyId,jdbcType=INTEGER}
    and
    wid.warehouse_id = #{warehouseId,jdbcType=INTEGER}

mapper.select:
select 

from wm_inbound_detail wid
where id = #{id,jdbcType=INTEGER}

select 

,bc.customer_name as supplier_name
from wm_inbound_detail wid
left join bd_customer bc on
	bc.customer_code = wid.supplier_code
and bc.company_id = wid.company_id
where wid.order_no = #{orderNo,jdbcType=VARCHAR}
and
wid.line_no = #{lineNo,jdbcType=VARCHAR}
and 
wid.company_id = #{companyId,jdbcType=INTEGER}
and
wid.warehouse_id = #{warehouseId,jdbcType=INTEGER}

select
max(cast(wid.line_no as signed))
from wm_inbound_detail wid 
where wid.order_no = #{orderNo,jdbcType=VARCHAR}
and 
wid.company_id = #{companyId,jdbcType=INTEGER}
and
wid.warehouse_id = #{warehouseId,jdbcType=INTEGER}

select

,bc.customer_name as supplier_name,
wih.is_calculated,
wih.status as header_status
from wm_inbound_header wih
left join bd_customer bc on
	bc.customer_code = wih.supplier_code
and bc.company_id = wih.company_id
left join wm_inbound_detail wid on
wid.order_no = wih.order_no
and wid.company_id = wih.company_id
and wid.warehouse_id = wih.warehouse_id

AND wih.status = #{headerStatus}
AND wih.is_calculated = #{isCalculated}
AND wih.inbound_type = #{inboundType}
AND wih.inbound_type = #{inboundType}
 AND wih.order_time >= #{orderTimeFm}
 AND wih.order_time <= #{orderTimeTo} 
order by wih.order_no,wid.line_no

SELECT sum( round(wid.inbound_price * wid.inbound_num,2) )as total, wid.supplier_code, bc.customer_name, bc.auxi_id FROM wm_inbound_detail wid LEFT JOIN bd_customer bc ON bc.company_id = wid.company_id AND bc.customer_code = wid.supplier_code LEFT JOIN wm_inbound_header wih ON wid.order_no = wih.order_no AND wid.company_id = wih.company_id AND wid.warehouse_id = wih.warehouse_id WHERE 1 = 1
AND wih.order_no = #{orderNo}
AND wih.status = '20' AND wid.company_id = #{companyId,jdbcType=INTEGER} AND wid.warehouse_id = #{warehouseId,jdbcType=INTEGER} GROUP BY wid.order_no

SELECT sum( round(wid.inbound_price * wid.inbound_num,2) )as total, wid.supplier_code, bc.customer_name, bc.auxi_id FROM wm_inbound_detail wid LEFT JOIN bd_customer bc ON bc.company_id = wid.company_id AND bc.customer_code = wid.supplier_code LEFT JOIN wm_inbound_header wih ON wid.order_no = wih.order_no AND wid.company_id = wih.company_id AND wid.warehouse_id = wih.warehouse_id WHERE 1 = 1
AND wid.order_no in
#{item}
AND wih.inbound_type = #{inboundType}
AND wih.status = '99' AND wid.company_id = #{companyId} AND wid.warehouse_id = #{warehouseId} GROUP BY wid.supplier_code

SELECT wih.order_no, wih.status as header_status, wid.status, wih.supplier_code, wih.order_time, bc.customer_name as supplier_name, wih.inbound_type, wid.line_no, bfs.fitting_sku_code, bfs.quick_code, bfs.fitting_sku_name, bfs.model_code, bfs.uom_desc, wid.is_cross_dock, wid.inbound_pre_num, wid.inbound_num, wid.inbound_price, cast(wid.inbound_num * wid.inbound_price as DECIMAL(9,2)) AS total_price, wid.plan_loc FROM wm_inbound_header wih LEFT JOIN wm_inbound_detail wid ON wid.order_no = wih.order_no AND wih.company_id = wid.company_id LEFT JOIN bd_fitting_sku bfs ON wid.sku_code = bfs.fitting_sku_code AND wid.company_id = bfs.company_id
LEFT JOIN bd_customer bc ON bc.customer_code = wid.supplier_code AND bc.company_id = wid.company_id
WHERE 1=1 AND bfs.fitting_sku_name LIKE CONCAT('%','${fittingSkuName}','%') AND bfs.fitting_sku_code = #{fittingSkuCode} AND bfs.model_code LIKE CONCAT('%','${modelCode}','%') AND wih.supplier_code = #{supplierCode} AND wih.status = #{headerStatus} AND wih.order_time >= #{orderTimeFm} AND wih.order_time <= #{orderTimeTo} AND wih.company_id = #{companyId} AND wih.warehouse_id = #{warehouseId}
AND wih.inbound_type in
#{item}
ORDER BY wih.order_time DESC, wid.line_no+0

mapper.delete:
DELETE FROM wm_inbound_detail
WHERE order_no =  #{orderNo}
AND company_id = #{companyId}
AND warehouse_id = #{warehouseId}
