mapper.sql:
WHERE 1=1
     AND wih.id = #{id}
     AND wih.order_no LIKE CONCAT('%','${orderNo}','%')
     AND wih.supplier_code = #{supplierCode}
     AND wih.audit_status = #{auditStatus}
     AND wih.status = #{status}
     AND wih.order_time >= #{orderTimeFm}
     AND wih.order_time <= #{orderTimeTo} 
     AND wih.is_calculated = #{isCalculated}
     AND wih.inbound_type = #{inboundType}
     AND woh.status IN (${multiStatus})
     AND wih.company_id = #{companyId}
     AND wih.warehouse_id = #{warehouseId}

WHERE 1=1
     AND wih.id = #{id}
     AND wih.order_no = #{orderNo}
     AND wih.supplier_code = #{supplierCode}
     AND wih.audit_status = #{auditStatus}
     AND wih.status = #{status}
     AND wih.inbound_type = #{inboundType}
     AND wih.is_calculated = #{isCalculated}
     AND wih.company_id = #{companyId}
     AND wih.warehouse_id = #{warehouseId}

