mapper.sql:
WHERE 1=1
     AND wir.id = #{id}
     AND wir.status = #{status}
     AND wir.order_no LIKE CONCAT('%', '${orderNo}','%')
     AND wir.line_no LIKE CONCAT('%','${lineNo}','%')
     AND wir.sku_code LIKE CONCAT('%','${skuCode}','%')
     AND wir.company_id = #{companyId}
     AND wir.warehouse_id = #{warehouseId}
     AND (wir.order_no LIKE CONCAT('%','${fuzzyCondition}','%')
    OR wir.line_no LIKE CONCAT('%','${fuzzyCondition}','%')
    OR wir.sku_code LIKE CONCAT('%','${fuzzyCondition}','%'))

WHERE 1=1
     AND wir.id = #{id}
     AND wir.status = #{status}
     AND wir.order_no = #{orderNo}
     AND wir.line_no = #{lineNo}
     AND wir.sku_code = #{skuCode}
     AND wir.inbound_loc_code = #{inboundLocCode}
     AND wir.company_id = #{companyId}
     AND wir.warehouse_id = #{warehouseId}

