mapper.select:
select 

from wm_inbound_recieve wir
where id = #{id,jdbcType=INTEGER}

select 

, bc.customer_name as supplier_name
from wm_inbound_recieve wir
left join bd_customer bc on
wir.supplier_code = bc.customer_code
and wir.company_id = bc.company_id
where wir.order_no = #{orderNo,jdbcType=VARCHAR}
and
wir.line_no = #{lineNo,jdbcType=VARCHAR}
and
wir.rec_line_no = #{recLineNo,jdbcType=VARCHAR}
and 
wir.company_id = #{companyId,jdbcType=INTEGER}
and
wir.warehouse_id = #{warehouseId,jdbcType=INTEGER}

select 

, bc.customer_name as supplier_name
from wm_inbound_recieve wir
left join bd_customer bc on
wir.supplier_code = bc.customer_code
and wir.company_id = bc.company_id
where wir.order_no = #{orderNo,jdbcType=VARCHAR}
and
wir.line_no = #{lineNo,jdbcType=VARCHAR}
and 
wir.company_id = #{companyId,jdbcType=INTEGER}
and
wir.warehouse_id = #{warehouseId,jdbcType=INTEGER}

select
max(wir.rec_line_no)
from wm_inbound_recieve wir 
where wir.order_no = #{orderNo,jdbcType=VARCHAR}
and
wir.line_no =  #{lineNo,jdbcType=VARCHAR}
and
wir.company_id = #{companyId,jdbcType=INTEGER}
and
wir.warehouse_id = #{warehouseId,jdbcType=INTEGER}

SELECT SUM(wir.cost*wir.inbound_num) as total FROM wm_inbound_recieve wir LEFT JOIN wm_inbound_header wih ON wir.order_no = wih.order_no AND wir.company_id = wih.company_id AND wir.warehouse_id = wih.warehouse_id WHERE 1 = 1
AND wir.order_no in
#{item}
AND wih.inbound_type = #{inboundType}
AND wih.status = '99' AND wir.company_id = #{companyId,jdbcType=INTEGER} AND wir.warehouse_id = #{warehouseId,jdbcType=INTEGER}
