mapper.sql:
WHERE 1=1
     AND wi.id = #{id}
     AND wi.sku_code  = #{skuCode}
     AND wi.lot = #{lot}
     AND wi.loc_code = #{locCode}
     AND bfs.fitting_sku_name LIKE CONCAT('%', '${skuName}','%')
     AND bfs.model_code LIKE CONCAT('%', '${modelCode}','%')
     AND wi.company_id = #{companyId}
     AND wi.warehouse_id = #{warehouseId}

WHERE 1=1
     AND wi.id = #{id}
     AND wi.sku_code  = #{skuCode}
     AND wi.lot = #{lot}
     AND wi.loc_code = #{locCode}
     AND wi.company_id = #{companyId}
     AND wi.warehouse_id = #{warehouseId}

mapper.select:
select
from wm_inventory wi WHERE id in
#{item}

mapper.update:
	UPDATE wm_inventory




                     when (ID=#{item.id}) then #{item.skuCode}




                     when (ID=#{item.id}) then #{item.lot}




                     when (ID=#{item.id}) then #{item.locCode}




                     when (ID=#{item.id}) then #{item.allocNum}




                     when (ID=#{item.id}) then #{item.invNum}




                     when (ID=#{item.id}) then #{item.invAvailableNum}




                     when (ID=#{item.id}) then #{item.preAssembleNum}




                     when (ID=#{item.id}) then #{item.totalPrice}




                     when (ID=#{item.id}) then #{item.modifier}




                     when (ID=#{item.id}) then #{item.modifyTime}




                     when (ID=#{item.id}) then #{item.recVer}




                     when (ID=#{item.id}) then #{item.companyId}




                     when (ID=#{item.id}) then #{item.warehouseId}



	WHERE


		(ID=#{item.id}) AND (REC_VER=#{item.recVer}-1)

mapper.select:
select 

from wm_inventory wi
where id = #{id,jdbcType=INTEGER}

select 

,bfs.fitting_sku_name as sku_name
,bfs.model_code
from wm_inventory wi
left join bd_fitting_sku bfs on
wi.sku_code = bfs.fitting_sku_code
and 
wi.company_id = bfs.company_id
where 1=1
and wi.sku_code = #{skuCode,jdbcType=VARCHAR}
and wi.loc_code = #{locCode,jdbcType=VARCHAR}
and wi.lot = #{lot,jdbcType=VARCHAR}
and wi.company_id = #{companyId,jdbcType=INTEGER}
and wi.warehouse_id = #{warehouseId,jdbcType=INTEGER}

SELECT SUM(wi.total_price)/SUM(wi.inv_available_num) FROM  wm_inventory wi
WHERE
1=1
and wi.sku_code = #{skuCode,jdbcType=VARCHAR}
and wi.company_id = #{companyId,jdbcType=INTEGER}
and wi.warehouse_id = #{warehouseId,jdbcType=INTEGER}
GROUP BY wi.sku_code

SELECT wi.inv_num,wi.loc_code,wi.sku_code FROM  wm_inventory wi
LEFT JOIN bd_loc bl ON bl.loc_code = wi.loc_code
AND bl.company_id = wi.company_id
WHERE
    1=1
AND bl.use_type = 'RS'
and wi.sku_code = #{skuCode,jdbcType=VARCHAR}
and wi.company_id = #{companyId,jdbcType=INTEGER}
and wi.warehouse_id = #{warehouseId,jdbcType=INTEGER}
ORDER BY wi.inv_num DESC,wi.modify_time DESC  LIMIT 1
