mapper.sql:
WHERE 1=1
     AND wod.id = #{id}
     AND wod.status = #{status}
     AND wod.status IN (${multiStatus})
     AND wod.order_no LIKE CONCAT('%', '${orderNo}','%')
     AND wod.line_no LIKE CONCAT('%','${lineNo}','%')
     AND wod.sku_code LIKE CONCAT('%','${skuCode}','%')
 AND wod.generic_sku_code = #{genericSkuCode}
     AND wod.is_create_cost_voucher = #{isCreateCostVoucher}
     AND wod.is_create_voucher = #{isCreateVoucher}
     AND wod.voucher_no LIKE CONCAT('%','${voucherNo}','%')
     AND wod.cost_voucher_no LIKE CONCAT('%','${costVoucherNo}','%')
     AND wod.company_id = #{companyId}
     AND wod.warehouse_id = #{warehouseId}
    AND (wod.order_no LIKE CONCAT('%','${fuzzyCondition}','%')
    OR wod.line_no LIKE CONCAT('%','${fuzzyCondition}','%')
    OR wod.sku_code LIKE CONCAT('%','${fuzzyCondition}','%'))

WHERE 1=1
     AND wod.id = #{id}
     AND wod.status = #{status}
     AND wod.order_no = #{orderNo}
     AND wod.line_no = #{lineNo}
     AND wod.sku_code = #{skuCode}
 AND wod.generic_sku_code = #{genericSkuCode}
     AND wod.is_create_cost_voucher = #{isCreateCostVoucher}
     AND wod.is_create_voucher = #{isCreateVoucher}
     AND wod.voucher_no = #{voucherNo}
     AND wod.cost_voucher_no = #{costVoucherNo}
     AND wod.company_id = #{companyId}
     AND wod.warehouse_id = #{warehouseId}

