mapper.select:
select 

from wm_outbound_detail wod
where id = #{id,jdbcType=INTEGER}

select
max(cast(wod.line_no as signed))
from wm_outbound_detail wod 
where wod.order_no = #{orderNo,jdbcType=VARCHAR}
and 
wod.company_id = #{companyId,jdbcType=INTEGER}
and
wod.warehouse_id = #{warehouseId,jdbcType=INTEGER}

    select
    wod.outbound_price,
	wod.modify_time
    from wm_outbound_detail wod
    where wod.buyer_code = #{buyerCode,jdbcType=VARCHAR}
    and
    wod.sku_code = #{skuCode,jdbcType=VARCHAR}
    and
    wod.status = '80'
    and 
    wod.company_id = #{companyId,jdbcType=INTEGER}
    and
    wod.warehouse_id = #{warehouseId,jdbcType=INTEGER}

select

,bc.customer_name as buyer_name,
woh.status as header_status
from wm_outbound_header woh
left join bd_customer bc on
	bc.customer_code = woh.buyer_code
and bc.company_id = woh.company_id
left join wm_outbound_detail wod on
wod.order_no = woh.order_no
and wod.company_id = woh.company_id
and wod.warehouse_id = woh.warehouse_id

AND woh.status = #{headerStatus}
AND woh.outbound_type = #{outboundType}
AND woh.is_calculated = #{isCalculated}
AND woh.is_cost_calculated = #{isCostCalculated}
 AND woh.order_time >= #{orderTimeFm}
 AND woh.order_time <= #{orderTimeTo} 
order by woh.order_no,wod.line_no

SELECT sum( round(wod.outbound_price * wod.outbound_num,2) )as total, wod.buyer_code, bc.customer_name, bc.auxi_id FROM wm_outbound_detail wod LEFT JOIN bd_customer bc ON bc.company_id = wod.company_id AND bc.customer_code = wod.buyer_code LEFT JOIN wm_outbound_header woh ON wod.order_no = woh.order_no AND wod.company_id = woh.company_id AND wod.warehouse_id = woh.warehouse_id WHERE 1 = 1
AND woh.order_no = #{orderNo}
AND wod.company_id = #{companyId,jdbcType=INTEGER} AND wod.warehouse_id = #{warehouseId,jdbcType=INTEGER} GROUP BY wod.order_no

SELECT sum( round(wod.outbound_price * wod.outbound_ship_num,2) )as total, wod.buyer_code, bc.customer_name, bc.auxi_id FROM wm_outbound_detail wod LEFT JOIN bd_customer bc ON bc.company_id = wod.company_id AND bc.customer_code = wod.buyer_code LEFT JOIN wm_outbound_header woh ON wod.order_no = woh.order_no AND wod.company_id = woh.company_id AND wod.warehouse_id = woh.warehouse_id WHERE 1 = 1
AND wod.order_no in
#{item}
AND woh.outbound_type = #{outboundType}
AND woh.status = '99' AND wod.company_id = #{companyId,jdbcType=INTEGER} AND wod.warehouse_id = #{warehouseId,jdbcType=INTEGER} GROUP BY wod.buyer_code

SELECT woh.order_no, woh. STATUS AS header_status, wod. STATUS, woh.buyer_code, bc.customer_name AS buyer_name, woh.outbound_type, woh.order_time, wod.line_no, bfs.fitting_sku_code, bfs.quick_code, bfs.fitting_sku_name, bfs.model_code, wod.outbound_num, cast(wod.outbound_num*wod.outbound_price as DECIMAL(9,2)) AS total_price, wod.outbound_alloc_num, wod.outbound_pick_num, wod.outbound_ship_num, wod.outbound_price, wod.cost, wod.is_cross_dock, wod.inbound_order_no, wod.inbound_line_no, wod.remark FROM wm_outbound_header woh LEFT JOIN wm_outbound_detail wod ON wod.order_no = woh.order_no AND woh.company_id = wod.company_id LEFT JOIN bd_fitting_sku bfs ON wod.sku_code = bfs.fitting_sku_code AND wod.company_id = bfs.company_id LEFT JOIN bd_customer bc ON bc.customer_code = wod.buyer_code AND bc.company_id = wod.company_id WHERE 1 = 1 AND bfs.fitting_sku_code = #{fittingSkuCode} AND bfs.fitting_sku_name LIKE CONCAT('%','${fittingSkuName}','%') AND bfs.model_code LIKE CONCAT('%','${modelCode}','%') AND wod.remark LIKE CONCAT('%','${remark}','%') AND woh.buyer_code = #{buyerCode} AND woh.status = #{headerStatus} AND woh.order_time >= #{orderTimeFm} AND woh.order_time <= #{orderTimeTo} AND woh.company_id = #{companyId} AND woh.warehouse_id = #{warehouseId}
AND woh.outbound_type in
#{item}
ORDER BY woh.order_time DESC, wod.line_no+0

mapper.delete:
DELETE FROM wm_outbound_detail
WHERE order_no =  #{orderNo}
AND company_id = #{companyId}
AND warehouse_id = #{warehouseId}

mapper.select:
SELECT
wod.outbound_price
FROM
wm_outbound_detail wod
LEFT JOIN wm_outbound_header woh ON wod.company_id = woh.company_id
AND wod.order_no = woh.order_no
WHERE
1 = 1

AND wod.sku_code = #{skuCode}


AND wod.buyer_code = #{buyerCode}

AND wod.company_id = #{companyId}
AND wod.warehouse_id = #{warehouseId}
ORDER BY
woh.order_time DESC
LIMIT 1;
